mapper.update:
UPDATE PHO_HT_ROOMTYPE
SET icount = icount + #{ROOMNUM}
WHERE
    mcid = #{MCID}
AND croomtypeid = #{ROOMTYPEID}
AND cstrdate = #{ENDDATE}

mapper.select:
SELECT
MIN(icount)
FROM
PHO_HT_ROOMTYPE
WHERE

    croomtypeid = #{ROOMTYPEID}
    AND cstrdate >= #{STARTDATE}
    AND cenddate <= #{ENDDATE}
    AND mcid = #{MCID}
